      //
			//  This function determines two things.
			//    -- If any driver is under the age of 21.   
			//    -- If the hiddenORM Flag should be set.
			//
			function doButtonChecks() {
			   checkHidden();
			   checkAges();
			}
			function checkHidden() {
			   if (typeof(document.forms[0].orm1Flag) != 'undefined') {
			      var j = document.forms[0].orm1Flag.selectedIndex;        
				    if (document.forms[0].orm1Flag.options[j].text == "Yes" ) {
				       document.forms[0].elements['ormhiddenflag'].value = "Yes";
			      } 
		     }		
		  }
		  function checkAges() {
         var i;
         for (i=1; i<=10; i++) {
            var dobString = 'date' + i + 'OfBirth';
            var checkNum = i-1;
            if (typeof(document.forms[0].elements[dobString]) != 'undefined')  {
               var dob = document.forms[0].elements[dobString].value;
               if (dob != undefined && dob.length == 10) {
                  if (getAge(dob) < 21) {
                     document.forms[0].elements['driverUnder21Flag'].value = "Yes";  
	                }
	             } 
	          }
         }
      }
      // 
      //  Determine age based the current date and the date of birth.
      //  Format of the date of birth is assumed to be MM/DD/YYYY.
      //
      function getAge(dob) {
	        var today = new Date();
	        var nowYear = today.getFullYear();
	        var nowMonth = today.getMonth();
	        var nowDay = today.getDate();
	        var age;
          dob = dob.split("/");
          var month = parseInt(dob[0],10)-1;
			    var day = parseInt(dob[1],10);
				  var year = parseInt(dob[2]);
				  age = nowYear - year;
	        if (month > nowMonth) {
		         age = age - 1;
	        } else if (month == nowMonth && nowDay < day) {
		         age = age - 1;	
	        } 
	        return age;
	    }
      //
			//  Validate date of Birth.  Because input should be date of birth,
			//  the year can't be after the current year.
			//
			function checkDOB(dob){
			   var d = dob;
			   if (d == '') {
			     return true;
			   }
			   var today = new Date(); 
		     var invalidMsg = ' ';
		     if (!/\d\d\/\d\d\/\d\d\d\d/.test(d) || d.length != 10)  {   
		        alert ("Please recheck your date format.  It should be MM/DD/YYYY.");
	          return false;
	       }			
			   d = d.split("/");
			   var year = parseInt(d[2]);
			   var month = parseInt(d[0],10)-1;
			   var day = parseInt(d[1],10);
			   //Check to see that the input fields are valid.
			   var dim =	daysInMonth(month+1, year);
	       if (day <1 || day > dim) {
			   invalidMsg = invalidMsg + 'Invalid Day. This is an invalid day for this year and month.  \n';
			   }
			   if (month <0 || month >11) {
			       invalidMsg = invalidMsg + 'Invalid Month.  Month must be between 01 and 12.  \n';
			   } 
			   var nowYear = today.getFullYear();
			   if ((year < 1600) || (year > nowYear)){
			       invalidMsg = invalidMsg + 'Invalid Year.  Year must be between 1900 and ' + nowYear + '. \n ';
			   } 
			   if (invalidMsg != ' ') {
				    alert(invalidMsg);
				    return false;
			   }
	       return true;
			}
			function daysInMonth(month,year) {  // months are 1-12
	       var dd = new Date(year, month, 0);
	       return dd.getDate();
      }
